function general() {
	$('.collapse-btn').on('click', function (e) {
		e.preventDefault();
		$(this).toggleClass('active')
		$(".collapse-wrap").slideToggle(500);

	});

	$('.short-by a').click(function () {
		$(this).toggleClass('active-short').siblings().removeClass('active-short');
	});
}
